const pickr=Pickr.create({el:"#colorpicker",theme:"monolith",default:"#808080",useAsButton:!0,swatches:["rgb(128, 128, 128)","rgb(156, 39, 176)","rgb(103, 58, 183)","rgb(63, 81, 181)","rgb(3, 169, 244)","rgb(0, 188, 212)","rgb(0, 150, 136)","rgb(76, 175, 80)","rgb(139, 195, 74)","rgb(205, 220, 57)","rgb(255, 235, 59)","rgb(255, 193, 7)","rgb(244, 67, 54)","rgb(233, 30, 99)"],components:{preview:!0,opacity:!0,hue:!0,interaction:{input:!0,save:!0}}});function setColor(r){let[o,t,e,c]=r.toRGBA();c>.9&&(c=.9);const i=r=>Math.round(r).toString(16).padStart(2,"0").toUpperCase(),n=Math.round(255*c).toString(16).padStart(2,"0").toUpperCase(),a=`#${i(o)}${i(t)}${i(e)}${n}`;$("#colorpicker").css("background-color",a),$("#colorEdificio").val(a)}pickr.on("change",r=>{setColor(r)}).on("save",r=>{setColor(r),pickr.hide()});